mapper.select:
  	select count(*) from tb_user_login 
  		
	  		
	  			and user_phone=#{userPhone}
	  		
	  		
	  			and user_name=#{userName}
	  		
	  		
	  			and user_email=#{userEmail}
	  		
	  		
	  			and user_id=#{userId}
	  		
	  		
	  			and user_img=#{userImg}
	  		
	  		
	  			and user_type=#{userType}
	  		
	  		
	  			and password=#{password}
	  		
	  		
	  			and user_describe=#{userDescribe}
